mapper.insert:
	
		SELECT NVL(MAX(bno), 100) + 1
		FROM board
	
	
	INSERT INTO board
			(
				bno
				, title
				
				, contents
				
				, writer
				
				, regdate
				
				
				, image
				
			)
			VALUES
			(
				#{bno}
				, #{title}
				
				, #{contents}
				
				, #{writer}
				
				
				, #{regdate}
				
				
				, #{image}
				
			)

mapper.update:
UPDATE board

	
		title = #{title}
	
	
		contents = #{contents}
	
	
		image = #{image}
	
	
		updatedate = sysdate
	
	
		updatedate = #{updatedate}
	

WHERE bno = #{bno}
